import React from 'react'
import { getAll, remove } from '../stateManagement/actions/productActions'
import { connect } from 'react-redux'


const reduxList = () => {
  return (
    <div>
      <h1>reduxList</h1>
      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>Id</th>
            <th>ProductName</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            {
              state.products.map(item =>
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.productName}</td>
                  <td>{item.price}</td>
                  <td>
                    <button className="btn btn-danger btn-sm">Delete</button>
                  </td>
                </tr>
              )
            }
          </tr>
        </tbody>
      </table>
    </div>
  )
}




//mapStateToProps => وصل کردن استیت داخل استور به یک پراپرتی  و خواندن اطلاعات
function mapStateToProps(state) {
  return {
    products: state.productState.items,
  };
}

// const mapDispatchToProps = dispatch => {
//   return{
//     removeRedux : (id) => dispatch(remove(id),)
//   }
// }

export default connect(mapStateToProps)(reduxList);
