import {ProductAdd , ProductGetById , ProductRemove , ProductGetAll} from "./actionType"

export const getAll = () =>({
    type: ProductGetAll,    
})

export const getById = id =>({
    type:ProductGetById,
    payload: id
})

export const add = item =>({
    type:ProductAdd,
    payload: item
}) 

export const remove = id =>({
    type:ProductRemove,
    payload: id
})
