import React, { useEffect } from 'react'
import { getAllProduct } from '../stateManagement/actions/productActions'
import { connect } from 'react-redux'


const userList = (props) => {

    useEffect(() => {
        props.getAll();
    }, []);

    return (
        <div>
            <h1>userList</h1>
            {
                props.isLoading ? <div>is Loading... </div> : null
            }
            <table className="table table-striped table-bordered">
                <thead>
                    <tr>
                        <th>Id</th>
                        <th>UserList</th>
                        <th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {props.users.map((item) => (
                        <tr key={item.id}>
                            <td>{item.id}</td>
                            <td>{item.name}</td>
                            <td>{item.username}</td>
                        </tr>
                    ))}

                </tbody>
            </table>
        </div>
    );
};




//mapStateToProps => وصل کردن استیت داخل استور به یک پراپرتی  و خواندن اطلاعات
function mapStateToProps(state) {
    return {
        users: state.userState.items,
        isLoading: state.userState.isLoading,
        message: state.userState.message,
    };
}


const mapDispatchToProps = dispatch => {
    return {
        getAll: (id) => getAllProduct(dispatch),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(userList);
