import { ProductAdd, ProductGetById, ProductRemove, ProductGetAll } from "../actions/actionType"


const initialState = {
    items: [
        { id: 1, productName: " Product 1", price: 100 },
        { id: 2, productName: " Product 2", price: 200 },
        { id: 3, productName: " Product 3", price: 300 },

    ]
};

function productReducer(state, action) {
    switch (action.type) {


        case ProductGetAll:
            return state.items;


        case ProductGetById:
            return state.items.find((query) => query.id === action.Payload);


        case ProductAdd:
            return state.items.concat([action.payload]);


        case ProductRemove:
            return state.items.filter(query => query.id !== action.payload);
        default:
            return state;
    }
}


export default productReducer;
